// Update a contract in the database with blockchain information
const updateContractWithBlockchainInfo = async (contractId, blockchainData) => {
  try {
    const contract = await Contract.findById(contractId);
    
    if (!contract) {
      throw new Error('Contract not found');
    }
    
    contract.blockchain = {
      isOnChain: true,
      contractAddress: blockchainData.address,
      transactionHash: blockchainData.transactionHash,
      network: blockchainData.network
    };
    
    await contract.save();
    return contract;
  } catch (error) {
    console.error('Error updating contract with blockchain info:', error);
    throw error;
  }
};

